      setEditingInvoice(null);
      loadData();
    } catch (error) {
      toast.error('Failed to save invoice');
    }
  };

  const handleDeleteExpense = async (id: string) => {
    if (window.confirm('Are you sure you want to delete this expense?')) {
      try {
        await apiService.deleteExpense(id);
        toast.success('Expense deleted successfully!');
        loadData();
      } catch (error) {
        toast.error('Failed to delete expense');
      }
    }
  };

  const handleDeleteInvoice = async (id: string) => {
    if (window.confirm('Are you sure you want to delete this invoice?')) {
      try {
        await apiService.deleteInvoice(id);
        toast.success('Invoice deleted successfully!');
        loadData();
      } catch (error) {
        toast.error('Failed to delete invoice');
      }
    }
  };

  if (!isAuthenticated) {
    return (
      <div className="min-h-screen flex items-center justify-center gradient-bg">
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          className="bg-white p-8 rounded-xl shadow-2xl max-w-md w-full mx-4"
        >
          <div className="text-center mb-6">
            <h1 className="text-3xl font-bold text-gray-800 mb-2">🔐 Secure Access</h1>
            <p className="text-gray-600">Enter password to access accounting system</p>
            <p className="text-sm text-gray-500 mt-2">Password: LimitEdge2024!</p>
          </div>
          <form onSubmit={handleLogin}>
            <div className="mb-6">
              <input
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                placeholder="Enter password"
                className="w-full px-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                required
              />
            </div>
            <button
              type="submit"
              className="btn-primary w-full"
            >
              Login
            </button>
          </form>
        </motion.div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Header */}
        <div className="flex justify-between items-center mb-8">
          <div>
            <h1 className="text-3xl font-bold text-gray-800">Accounting System</h1>
            <p className="text-gray-600">Manage expenses and invoices for Limited Edge Budget</p>
          </div>
          <button
            onClick={handleLogout}
            className="btn-secondary"
          >
            Logout
          </button>
        </div>

        {/* Tabs */}
        <div className="flex space-x-1 mb-8">
          <button
            onClick={() => setActiveTab('expenses')}
            className={`px-6 py-3 rounded-lg font-medium transition-colors ${
              activeTab === 'expenses'
                ? 'bg-blue-500 text-white'
                : 'bg-white text-gray-600 hover:bg-gray-50'
            }`}
          >
            Expenses
          </button>
          <button
            onClick={() => setActiveTab('invoices')}
            className={`px-6 py-3 rounded-lg font-medium transition-colors ${
              activeTab === 'invoices'
                ? 'bg-blue-500 text-white'
                : 'bg-white text-gray-600 hover:bg-gray-50'
            }`}
          >
            Invoices
          </button>
          <button
            onClick={() => setActiveTab('reports')}
            className={`px-6 py-3 rounded-lg font-medium transition-colors ${
              activeTab === 'reports'
                ? 'bg-blue-500 text-white'
                : 'bg-white text-gray-600 hover:bg-gray-50'
            }`}
          >
            Reports & Export
          </button>
        </div>

        {/* Content */}
        {activeTab === 'expenses' ? (
          <ExpenseManagement
            expenses={expenses}
            subCategories={subCategories}
            onAddExpense={() => setShowExpenseForm(true)}
            onEditExpense={(expense) => {
              setEditingExpense(expense);
              setShowExpenseForm(true);
            }}
            onDeleteExpense={handleDeleteExpense}
          />
        ) : activeTab === 'invoices' ? (
          <InvoiceManagement
            invoices={invoices}
            onAddInvoice={() => setShowInvoiceForm(true)}
            onEditInvoice={(invoice) => {
              setEditingInvoice(invoice);
              setShowInvoiceForm(true);
            }}
            onDeleteInvoice={handleDeleteInvoice}
            onExportInvoice={exportInvoiceToPDF}
            onPreviewInvoice={previewInvoice}
          />
        ) : (
          <ReportsManagement
            expenses={expenses}
            invoices={invoices}
            subCategories={subCategories}
            reportType={reportType}
            setReportType={setReportType}
            dateRange={dateRange}
            setDateRange={setDateRange}
            onExportCSV={exportToCSV}
            onPrintReport={printReport}
          />
        )}

        {/* Forms */}
        {showExpenseForm && (
          <ExpenseForm
            expense={editingExpense}
            subCategories={subCategories}
            onSubmit={handleExpenseSubmit}
            onClose={() => {
              setShowExpenseForm(false);
              setEditingExpense(null);
            }}
          />
        )}

        {showInvoiceForm && (
          <InvoiceForm
            invoice={editingInvoice}
            onSubmit={handleInvoiceSubmit}
            onClose={() => {
              setShowInvoiceForm(false);
              setEditingInvoice(null);
            }}
            generateInvoiceNumber={generateUniqueInvoiceNumber}
          />
        )}
      </div>
    </div>
  );
};

