import React, { useState } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { Bars3Icon, XMarkIcon, ChevronDownIcon } from '@heroicons/react/24/outline';

const Navbar: React.FC = () => {
  const [isOpen, setIsOpen] = useState(false);
  const [isDropdownOpen, setIsDropdownOpen] = useState(false);
  const location = useLocation();

  const mainNavigation = [
    { name: 'Home', href: '/', icon: '🏠' },
    { name: 'Budget Overview', href: '/budget-overview', icon: '📊' },
    { name: 'Accounting System', href: '/accounting', icon: '💰' },
    { name: 'The Challenge', href: '/the-challenge', icon: '🏆' },
  ];

  const productionNavigation = [
    { name: 'Teaser Pilot', href: '/teaser-pilot', icon: '🎬' },
    { name: 'Pre-Production', href: '/pre-production', icon: '📋' },
    { name: 'Production', href: '/production', icon: '🎥' },
    { name: 'Post-Production', href: '/post-production', icon: '✂️' },
  ];

  const businessNavigation = [
    { name: 'Investor Benefits', href: '/investor-benefits', icon: '💼' },
    { name: 'Marketing', href: '/marketing', icon: '📢' },
    { name: 'Contingency', href: '/contingency', icon: '⚠️' },
    { name: 'Participant Requirements', href: '/participant-requirements', icon: '👥' },
  ];

  return (
    <nav className="gradient-bg shadow-lg">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between h-16">
          <div className="flex items-center">
            <Link to="/" className="flex-shrink-0 flex items-center">
              <img
                className="h-8 w-8 rounded-full"
                src="/images/logo.jpg"
                alt="Limited Edge Budget"
              />
              <span className="ml-2 text-white font-bold text-xl">
                Limited Edge Budget
              </span>
            </Link>
          </div>

          {/* Desktop Navigation */}
          <div className="hidden md:flex items-center space-x-4">
            {navigation.map((item) => (
              <Link
                key={item.name}
                to={item.href}
                className={`px-3 py-2 rounded-md text-sm font-medium transition-colors duration-200 ${
                  location.pathname === item.href
                    ? 'bg-white bg-opacity-20 text-white'
                    : 'text-gray-200 hover:text-white hover:bg-white hover:bg-opacity-10'
                }`}
              >
                {item.name}
              </Link>
            ))}
          </div>

          {/* Mobile menu button */}
          <div className="md:hidden flex items-center">
            <button
              onClick={() => setIsOpen(!isOpen)}
              className="text-gray-200 hover:text-white focus:outline-none focus:text-white"
            >
              {isOpen ? (
                <XMarkIcon className="h-6 w-6" />
              ) : (
                <Bars3Icon className="h-6 w-6" />
              )}
            </button>
          </div>
        </div>

        {/* Mobile Navigation */}
        {isOpen && (
          <div className="md:hidden">
            <div className="px-2 pt-2 pb-3 space-y-1 sm:px-3 bg-white bg-opacity-10 rounded-lg mt-2">
              {navigation.map((item) => (
                <Link
                  key={item.name}
                  to={item.href}
                  className={`block px-3 py-2 rounded-md text-base font-medium transition-colors duration-200 ${
                    location.pathname === item.href
                      ? 'bg-white bg-opacity-20 text-white'
                      : 'text-gray-200 hover:text-white hover:bg-white hover:bg-opacity-10'
                  }`}
                  onClick={() => setIsOpen(false)}
                >
                  {item.name}
                </Link>
              ))}
            </div>
          </div>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
